import './store.css';
import { Carousel } from 'react-bootstrap';
import { useState } from "react";
import tile1 from '../../images/IMG-3162.jpg'
import tile2 from '../../images/IMG-3156.jpg'
import tile3 from '../../images/IMG-3150.jpg'

export default function Preview() {

  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

   return (
    <div className="previewCont">
      <div className="previewTile">
        <Carousel className="carousel_cont" variant="light" fade 
            activeIndex={index} onSelect={handleSelect}>
          <Carousel.Item interval={2000}>
            <img
              className="d-block w-100 carousel_img"
              src={tile1}
              alt="First slide"
            />
          </Carousel.Item>
          <Carousel.Item interval={2000}>
            <img
              className="d-block w-100 carousel_img"
              src={tile2}
              alt="Second slide"
            />
          </Carousel.Item>
          <Carousel.Item interval={2000}>
            <img
              className="d-block w-100 carousel_imgÂ§"
              src={tile3}
              alt="Third slide"
            />
          </Carousel.Item>
        </Carousel>
        <div className="previewTxtBox">
        </div>
      </div>
      <div className="basketBox">
        <div className="basketBtc" onClick={()=> navigate('/checkout')}>Add to baskey</div>
      </div>
    </div>
  );
}
