import './login.css';
import { useState } from 'react';
import axios from "axios";
import { toast } from "react-toastify";
import { setAxiosAuthToken, toastOnError } from "./axios.js";

export default function Login() {

  const [username, setUsername] = useState()
  const [password, setPassword] = useState()

  const submit = () => {
    const userData = {
      username: username,
      password: password
    }
    login(userData, "/");
    console.log(userData)
  }

  const userInput = event => {
    setUsername(event.target.value)
  }

  const passInput = event => {
    setPassword(event.target.value)
  }

  const login = (userData, redirectTo) => dispatch => {
  axios
    .post("/api/v1/token/login/", userData)
    .then(response => {
      const { auth_token } = response.data;
      setAxiosAuthToken(auth_token);
      // dispatch(setToken(auth_token));
      // dispatch(getCurrentUser(redirectTo));
    })
    .catch(error => {
      // dispatch(unsetCurrentUser());
      toastOnError(error);
    });
};


return (
  <div className="adminCont">
    <div className="usernameCont">
      <div className="usernameTitle">Username:</div>
      <input className="username" type="text" onChange={userInput}/>
    </div>
    <div className="passwordCont">
      <div className="passwordTitle">Password:</div>
      <input className="password" type="password" onChange={passInput}/>
    </div>  
    <div className="login" onClick={submit}>Login</div>
  </div>
)}
